  #{visitorName,jdbcType=VARCHAR}, #{visitorPhonenumber,jdbcType=VARCHAR}, #{visitorIdcard,jdbcType=VARCHAR}, 
  #{visitorCompany,jdbcType=VARCHAR}, #{entryTime,jdbcType=TIMESTAMP}, #{departureTime,jdbcType=TIMESTAMP}
  )

mapper.select:
