mapper.select:
select vr.visitor_regist_id,

from visitor_info vi, visitor_registration vr
where
  vr.visitor_regist_id = vi.visitor_regist_id

  and vr.lessee_id = #{lesseeId}


  and vr.visitor_regist_id = #{visitorRegistId}


  and vi.visitor_info_id = #{visitorInfoId}


  and vr.receiver_name = "%"#{receiverName}"%"


  and vr.receiver_phone_number = #{receiverPhoneNumber}


  and vi.visitor_name = "%"#{visitorName}"%"


  and vr.visiting_time between #{vistingStartTime} and #{visitingEndTime}


  and vr.visiting_time >= #{vistingStartTime}


  and vr.visiting_time <= #{visitingEndTime}

order by vr.creation_time desc

select vr.visitor_regist_id as visitorRegistId,
       vi.visitor_name as visitorName,
       vi.visitor_type as visitorType,
       visiting_time as visitingTime,
       visitor_purpose as visitorPurpose,
       vr.receiver_name as receiverName,
       vr.process_approval_status as processApprovalStatus

from visitor_info vi, visitor_registration vr
where
vi.visitor_regist_id = vr.visitor_regist_id

  and vr.lessee_id = #{lesseeId}


  and vr.visitor_regist_id = #{visitorRegistId}


  and vi.visitor_info_id = #{visitorInfoId}


  and vr.receiver_name = "%"#{receiverName}"%"


  and vr.receiver_phone_number = #{receiverPhoneNumber}


  and vi.visitor_name = "%"#{visitorName}"%"


  and vr.visiting_time between #{vistingStartTime} and #{visitingEndTime}


  and vr.visiting_time >= #{vistingStartTime}


  and vr.visiting_time <= #{visitingEndTime}

order by vr.creation_time desc
